package blanutsa.dmitriy;public class StringUtils {    public static final int MIN_LEXEME_LENGTH = 3;    public static final int MAX_LEXEME_LENGTH = 5;    public static String cleanRow(String row) {        String[] lexemes = row.split("\\s");        for (String lexeme : lexemes) {            if (!lexeme.equals("") && lexeme.length() >= MIN_LEXEME_LENGTH && lexeme.length() <= MAX_LEXEME_LENGTH) {                row = row.replace(lexeme, "");                System.out.println(String.format("      Removed \"%s\"", lexeme));            }        }        return row;    }}