package blanutsa.dmitriy.config;import java.io.IOException;import java.io.InputStream;import java.util.Properties;public class ApplicationProperties {    public static final String propertiesFilePath = System.getProperty("user.dir") + "/src/main/resources/application.properties";    private static Properties properties;    private static InputStream inputStream;    static {        properties = new Properties();        inputStream = ApplicationProperties.class.getClassLoader().getResourceAsStream("application.properties");        try {            properties.load(inputStream);        } catch (IOException e) {            e.printStackTrace();        }    }    public static String getProperty(String key) {        return properties.getProperty(key);    }}