package blanutsa.dmitriy.controller;import blanutsa.dmitriy.controller.command.Command;import blanutsa.dmitriy.controller.command.CommandList;import blanutsa.dmitriy.view.View;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;@WebServlet(urlPatterns = "/mainServlet")public class MainController extends HttpServlet {    private static final long serialVersionUID = 7023872695729819951L;    private final View view = new View();    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) {        String commandName = request.getParameter("command");        Command command = CommandList.valueOf(commandName).getCommand();        String goTo = command.execute(request, response);        view.forward(request, response, goTo);    }}