package blanutsa.dmitriy.controller;import lombok.Getter;import lombok.Setter;import java.io.Serializable;import java.util.List;@Getter@Setterpublic class TableResponse<D> implements Serializable {    private static final long serialVersionUID = 4221077279849465967L;        private int page = 0;    private int size = 10;    private List<D> list;    public int getPage() {        return page;    }    public void setPage(int page) {        this.page = page;    }    public int getSize() {        return size;    }    public void setSize(int size) {        this.size = size;    }    public List<D> getList() {        return list;    }    public void setList(List<D> list) {        this.list = list;    }}