package blanutsa.dmitriy.db;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;import static blanutsa.dmitriy.config.ApplicationProperties.*;public class ConnectionPool {    private static Connection connection;    public static Connection getConnection() {        try {            Class.forName("org.postgresql.Driver");        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        try {            connection = DriverManager.getConnection(                    getProperty("database.url"),                    getProperty("database.user"),                    getProperty("database.password")            );        } catch (SQLException e) {            e.printStackTrace();        }        return connection;    }}