package blanutsa.dmitriy.models;import blanutsa.dmitriy.db.anotations.Lazy;import blanutsa.dmitriy.models.enums.CarType;import lombok.Getter;import lombok.Setter;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import javax.validation.constraints.Past;import java.util.Date;import java.util.List;@Getter@Setterpublic class Car extends BaseEntity {    @NotBlank    @Length(max = 50)    private String number;    @NotNull    private CarType type;    @NotBlank    @Length(max = 50)    private String manufacturer;    @NotBlank    @Length(max = 50)    private String model;    @NotNull    @Past    private Date productYear;    @Lazy    private List<Picture> pictures;    @NotNull    @Lazy    private Person owner;    @NotNull    private Long ownerId;    private List<AttorneyPower> attorneyPowers;}