package blanutsa.dmitriy.models;import blanutsa.dmitriy.db.anotations.Lazy;import lombok.Getter;import lombok.Setter;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import javax.validation.constraints.Past;import java.util.Date;import java.util.List;@Getter@Setterpublic class Person extends BaseEntity {    @NotNull    private Long id;    @NotBlank    @Length(max = 50)    private String firstName;    @NotBlank    @Length(max = 50)    private String lastName;    @NotNull    @Past    private Date birthDate;    @Lazy    private List<AttorneyPower> attorneyPowers;    @Lazy    private Car car;    private String carNumber;}