package blanutsa.dmitriy.models;import lombok.Getter;import lombok.Setter;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;@Getter@Setterpublic class Picture extends BaseEntity {    @NotNull    private Integer id;    @Length(max = 256)    private String title;    @NotBlank    @Length(max = 100)    private String path;    private boolean main;    @NotNull    private String carNumber;}