package blanutsa.dmitriy.services.baseQuery;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.Setter;import lombok.ToString;import org.hibernate.validator.constraints.Length;import javax.validation.constraints.NotBlank;@Getter@Setter@AllArgsConstructor@ToStringpublic class Order {    @NotBlank    @Length(max = 20)    private String field;    private OrderType type = OrderType.ASC;    @Override    public String toString() {        return field + " " + type.toString();    }}