package blanutsa.dmitriy.services.baseQuery;import java.util.List;public class QueryBuilder {    public static final char alias = 'o';    private StringBuilder query = new StringBuilder();    public QueryBuilder select(List<String> selections) {        query.append("SELECT ").                append(String.join(", ", selections));        return this;    }    public QueryBuilder from(Class entityClass) {        query.append("FROM ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder insert(Class entityClass) {        query.append("INSERT INTO ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder update(Class entityClass) {        query.append("UPDATE ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder delete(Class entityClass) {        query.append("DELETE FROM ");        tableNamePart(entityClass);        return this;    }    public QueryBuilder order(List<Order> orders) {        query.append("ORDER BY ");        return this;    }    private void tableNamePart(Class entityClass) {        query.append(entityClass.getSimpleName()).                append(" ").                append(alias).                append(" ");    }}