CREATE TABLE car (  created_at TIMESTAMP NOT NULL,  updated_at TIMESTAMP,  number VARCHAR(50) PRIMARY KEY,  type VARCHAR(20) NOT NULL,  manufacturer VARCHAR(50) NOT NULL,  model VARCHAR(50) NOT NULL,  product_year TIMESTAMP,  owner_id int NOT NULL REFERENCES person(id));CREATE TABLE person (  created_at TIMESTAMP NOT NULL,  updated_at TIMESTAMP,  id SERIAL PRIMARY KEY,  first_name VARCHAR(50) NOT NULL,  last_name VARCHAR(50) NOT NULL,  birth_date TIMESTAMP,  car_number VARCHAR(50));CREATE TABLE picture (  created_at TIMESTAMP NOT NULL,  updated_at TIMESTAMP,  id serial PRIMARY KEY,  title VARCHAR(256),  path VARCHAR(100) NOT NULL,  main BOOLEAN,  car_number VARCHAR(50) NOT NULL REFERENCES car(number));CREATE TABLE attorney_power (  created_at TIMESTAMP NOT NULL,  updated_at TIMESTAMP,  id SERIAL PRIMARY KEY,  person_id INT NOT NULL REFERENCES person(id),  car_number VARCHAR(50) NOT NULL REFERENCES car(number),  from_date TIMESTAMP NOT NULL,  to_date TIMESTAMP NOT NULL);ALTER TABLE personALTER COLUMN car_number TYPE VARCHAR(50) USING NOT NULL;